import unittest


class MajorityElement:
    def __init__(self) -> None:
        pass
    # end def

    def majorityElement(self, nums: list[int]) -> int:
        if len(nums) <= 1:
            return nums[0]

        half: int = len(nums) / 2 if len(nums) % 2 == 0 else int(len(nums) / 2) + 1

        nums = sorted(nums)
        slow: int = 0
        fast: int = 1

        while fast < len(nums):
            if nums[slow] == nums[fast]:
                fast += 1
                continue

            if fast - slow >= half:
                return nums[slow]

            slow = fast
            fast += 1
        # end loop

        if fast - slow >= half:
            return nums[slow]

        return None
    # end def
# end class


class TestClass(unittest.TestCase):
    def setUp(self):
        self.__solution = MajorityElement()
    # end def

    def test_case1(self):
        '''test case1'''
        # arrange
        nums = [3, 2, 3]

        # act
        result = self.__solution.majorityElement(nums)

        # assert
        expect = 3
        self.assertEqual(expect, result)
    # end def

    def test_case2(self):
        '''test case2'''
        # arrange
        nums = [2, 2, 1, 1, 1, 2, 2]

        # act
        result = self.__solution.majorityElement(nums)

        # assert
        expect = 2
        self.assertEqual(expect, result)
    # end def

